import React, { Component } from "react";

class UserDetail extends Component {
  constructor() {
    super();
    this.state = {
      city: "Salford"
    };
  }

  changeCity = (e) => {
    e.preventDefault();
    this.setState({
      city: document.getElementById("cityInput").value
    })
  };

  render() {
    return (
      <div>
        <p>this is the UserDetail Component</p>
        <p>date of birth: {this.props.userData.dob} </p>
        <p>city: {this.state.city} </p>
        {/* <div>{this.loopItem(this.props.userData)}</div>; */}
        <form >
          <input type="text" id="cityInput"/>
          <button type="button" onClick={this.changeCity}>change city</button>
        </form>
      </div>
    );
  }
  loopItem = someData => {
    let mydiv = document.createElement("div");
    for (let item in someData) {
      if (someData.hasOwnProperty(item)) {
        let listItem = document.createElement("li");
        listItem.innerText = `${item}: ${someData[item]}`;
        mydiv.appendChild(listItem);
        console.log(mydiv);
      }
    }
  };
}

export default UserDetail;
